import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import './App.css';
import axios from 'axios';
import Home from './components/home';
import MovieLayout from './components/MovieLayout';
import Navbar from './components/Navbar';

function App() {
	const [movies, setMovies] = useState([]);

	let film = {
		method: 'GET',
		url:
			'https://imdb-internet-movie-database-unofficial.p.rapidapi.com/film/tt1375666',
		headers: {
			'content-type': 'application/octet-stream',
			'x-rapidapi-host':
				'imdb-internet-movie-database-unofficial.p.rapidapi.com',
			'x-rapidapi-key': '1928aa76c3msh12d0543b8640813p1b3c8cjsneb28e6b3b6ed'
		}
	};

	let search = {
		method: 'GET',
		url:
			'https://imdb-internet-movie-database-unofficial.p.rapidapi.com/search/inception',
		headers: {
			'content-type': 'application/octet-stream',
			'x-rapidapi-host':
				'imdb-internet-movie-database-unofficial.p.rapidapi.com',
			'x-rapidapi-key': '1928aa76c3msh12d0543b8640813p1b3c8cjsneb28e6b3b6ed'
		}
	};

	useEffect(() => {
		axios(film)
			.then(res => setMovies(res.data))
			.catch(err => console.error(err));
	}, []);

	return (
		<Router>
			<Switch>
				<Navbar />
				<div className='container'>
					<MovieLayout movie={movies} />
				</div>
			</Switch>
		</Router>
	);
}

export default App;
