import React, { useEffect, useState } from 'react';
import { SearchContext } from './Search';

function MovieLayout(props) {
	const {
		searchResult,
		variable,
		handleSearchInput,
		handleSearchSubmit
	} = React.useContext(SearchContext);

	const [isThereMovie, setIsThereMovie] = useState(false);

	// result destructured
	const [title, setTitle] = useState([]);
	const [character, setCharacter] = useState([]);
	const [company, setCompany] = useState([]);

	useEffect(() => {
		let resultNotEmpty = Object.keys(searchResult).length === 0;
		if (!resultNotEmpty) {
			const { newTitle, newCharacter, newCompany } = searchResult;
			setTitle(newTitle);
			setCharacter(newCharacter);
			setCompany(newCompany);
			setIsThereMovie(true);
		}
	}, [searchResult]);

	const displayTitle = title => {
		return (
			<section className='col-md-4'>
				<div className='title'>
					<h2 className='pb-2 text-center'>title</h2>
					<div className='row'>
						<div className='col-5'>
							<img src='' className='img-fluid' title='the image' />
						</div>
						<div className='col-7'>
							<h4>Movie Name</h4>
						</div>
					</div>
				</div>
			</section>
		);
	};

	const displayCharacter = character => {
		return (
			<section className='col-md-4'>
				<div className='title'>
					<h2 className='pb-2 text-center'>Character</h2>
					<div className='row'>
						<div className='col-5'>
							<img src='' className='img-fluid' title='the image' />
						</div>
						<div className='col-7'>
							<h4>Character Name</h4>
						</div>
					</div>
				</div>
			</section>
		);
	};

	const displayCompany = company => {
		return (
			<section className='col-md-4'>
				<div className='title'>
					<h2 className='pb-2 text-center'>Companies</h2>
					<div className='row'>
						<div className='col-5'>
							<img src='' className='img-fluid' title='the image' />
						</div>
						<div className='col-7'>
							<h4>Company Name</h4>
						</div>
					</div>
				</div>
			</section>
		);
	};

	return (
		<>
			{isThereMovie ? (
				<div className='row py-5'>
					{/* title section */}
					{title.map(title => displayTitle(title))}
					{/* character section */}
					{character.map(character => displayCharacter(character))}
					{/* company section */}
					{company.map(company => displayCompany(company))}
				</div>
			) : (
				<div className='spinPlaceholder'>
					<div className='m-auto'>
						<div className='spinner-grow text-muted'></div>
					</div>
				</div>
			)}
		</>
	);
}

export default MovieLayout;
