import React, { useEffect, useState } from 'react';
import { SearchContext } from './Search';

function MovieLayout(props) {
	const {
		searchResult,
		variable,
		handleSearchInput,
		handleSearchSubmit
	} = React.useContext(SearchContext);

	const [movie, setMovie] = useState({});
	const [isThereMovie, setIsThereMovie] = useState(false);

	useEffect(() => {
		let resultNotEmpty = Object.keys(searchResult).length === 0;
		if (!resultNotEmpty) {
			setIsThereMovie(true);
			setMovie(searchResult);
			console.log(searchResult);
		}
	}, [searchResult]);

	const displayTitle = title => {
		return (
			<section className='col-md-4'>
				<div className='title'>
					<h2 className='pb-2 text-center'>title</h2>
					<div className='row'>
						<div className='col-5'>
							<img src='' className='img-fluid' title='the image' />
						</div>
						<div className='col-7'>
							<h4>Movie Name</h4>
						</div>
					</div>
				</div>
			</section>
		);
	};

	return (
		<>
			{isThereMovie ? (
				<div className='row py-5'>
					{/* character section */}
					<section className='col-md-4'>
						<div className='title'>
							<h2 className='pb-2 text-center'>Character</h2>
							<div className='row'>
								<div className='col-5'>
									<img src='' className='img-fluid' title='the image' />
								</div>
								<div className='col-7'>
									<h4>Character Name</h4>
								</div>
							</div>
						</div>
					</section>
					{/* company section */}
					<section className='col-md-4'>
						<div className='title'>
							<h2 className='pb-2 text-center'>Companies</h2>
							<div className='row'>
								<div className='col-5'>
									<img src='' className='img-fluid' title='the image' />
								</div>
								<div className='col-7'>
									<h4>Company Name</h4>
								</div>
							</div>
						</div>
					</section>
				</div>
			) : (
				<div className='spinPlaceholder'>
					<div className='m-auto'>
						<div class='spinner-grow text-muted'></div>
					</div>
				</div>
			)}
		</>
	);
}

export default MovieLayout;
