import React, { useEffect, useState } from 'react';
import { SearchContext } from './Search';

function MovieLayout(props) {
	const {
		searchResult,
		variable,
		handleSearchInput,
		handleSearchSubmit
	} = React.useContext(SearchContext);

	const [isThereMovie, setIsThereMovie] = useState(false);

	// result destructured
	const [title, setTitle] = useState([]);
	const [character, setCharacter] = useState([]);
	const [company, setCompany] = useState([]);

	useEffect(() => {
		let resultNotEmpty = Object.keys(searchResult).length === 0;
		if (!resultNotEmpty) {
			console.log(searchResult);
			const { companies, names, titles } = searchResult;
			setTitle(titles);
			setCharacter(names);
			setCompany(companies);
			setIsThereMovie(true);
		}
	}, [searchResult]);

	const displayTitle = title => {
		console.log(title);
		return (
			<section className='col-md-4'>
				<div className='title'>
					<h2 className='pb-2 text-center'>title</h2>
					<div className='row'>
						<div className='col-5'>
							<img src='' className='img-fluid' title='the image' />
						</div>
						<div className='col-7'>
							<h4>Movie Name</h4>
						</div>
					</div>
				</div>
			</section>
		);
	};

	const displayCharacter = character => {
		console.log(character);
		return (
			<div key={character.id} className='row py-2'>
				<div className='col-5'>
					<img src={character.image} className='img-fluid' title='the image' />
				</div>
				<div className='col-7'>
					<h4>{character.title} </h4>
				</div>
			</div>
		);
	};

	const displayCompany = company => {
		return (
			<div key={company.id} className='col-12'>
				<h4>{company.title}</h4>
			</div>
		);
	};

	return (
		<>
			{isThereMovie ? (
				<div className='row py-5'>
					{/* title section */}
					{title.map(title => displayTitle(title))}

					{/* character section */}
					<section className='col-md-4'>
						<div className='title'>
							<h2 className='pb-2 text-center'>Character</h2>
							{character.map(character => displayCharacter(character))}
						</div>
					</section>

					{/* company section */}
					<section className='col-md-4'>
						<div className='title'>
							<h2 className='pb-2 text-center'>Companies</h2>
							<div className='row'>
								{company.map(company => displayCompany(company))}
							</div>
						</div>
					</section>
				</div>
			) : (
				<div className='spinPlaceholder'>
					<div className='m-auto'>
						<div className='spinner-grow text-muted'></div>
					</div>
				</div>
			)}
		</>
	);
}

export default MovieLayout;
