import React from 'react';

function MovieLayout({ movie }) {
	return (
		<div className='col-md-4 col-sm-6'>
			<div className='row'>
				<div className='col-12'>
					<img src={movie.poster} title={movie.title} />
				</div>
				<div className='col-12'>
					<h3>{movie.title}</h3>
				</div>
				<div className='col-12'>
					<p>{movie.plot}</p>
				</div>
			</div>
		</div>
	);
}

export default MovieLayout;
